<?phpuse App\Http\Controllers\DashboardController;use App\Http\Controllers\HomeController;use Illuminate\Support\Facades\Route;Route::controller(HomeController::class)->group(function (){    Route::get('/',  'showHome')->name('home');    Route::get('/home',  'showHome');    Route::get('/donate',  'showDonatePage')->name('donate');    Route::get('/about', 'showAboutPage')->name('about');});Route::controller(DashboardController::class)->group(function (){    Route::get('/dashboard', 'showDashboard')->name('dashboard');});Auth::routes();Route::get('/users', function (){    return view('admin.users');})->name('users');